import React, { Component } from 'react';
import { Alert, ActivityIndicator, Keyboard, KeyboardAvoidingView, StyleSheet } from 'react-native';

import { Button, Block, Input, Text } from '../components';
import { theme } from '../constants';



export default class Singup extends Component {
    
    state = {
        email: null,
        username: null,
        password: null,
        errors: [],
        loading: false,
    }

    handleSignup() {
        const { navigation } = this.props;
        const { email, username, password } = this.state;
        const errors = [];

        Keyboard.dismiss(); 
        this.setState({ errors: [], loading: true });

        if (!email) errors.push('email');
        if (!username) errors.push('username');
        if (!password) errors.push('password');
        


        this.setState({ errors, loading: false });

        if (!errors.length) {
            Alert.alert(
                'Success!',
                'Your account has been created.',
                [
                    {
                        text: 'Continue', onPress: () => {
                            navigation.navigate('Browse')
                        }
                    }
                ],
                { cancelable: false }
            )
        } 
    }


    render() {
        const { navigation } = this.props;
        const { loading, errors } = this.state;
        const hasErrors = key => errors.includes(key) ? styles.hasErrors : null;
        return (
            <KeyboardAvoidingView style={styles.signup} behavior="height">
                <Block padding={[0, theme.sizes.base * 2]}>
                    <Text h1 bold>Signup</Text>
                    <Block middle>
                        <Input
                            email
                            label="Email"
                            error={hasErrors('email')}
                            style={[styles.input, hasErrors('email')]}
                            defaultValue={this.state.email}
                            onChangeText={text => this.setState({ email: text })}
                        />
                        <Input
                            label="Username"
                            error={hasErrors('username')}
                            style={[styles.input, hasErrors('username')]}
                            defaultValue={this.state.username}
                            onChangeText={text => this.setState({ username: text })}
                        />
                        <Input
                            secure
                            label="Password"
                            error={hasErrors('password')}
                            style={[styles.input, hasErrors('password')]}
                            defaultValue={this.state.password}
                            onChangeText={text => this.setState({ password: text })}
                        />


                        <Button gradient onPress={() => this.handleSignup()}>
                            {loading ? <ActivityIndicator size="small" color="white" /> :
                                <Text bold white center>Signup</Text>
                            }
                        </Button>
                        <Text center onPress={() => navigation.goBack()}>
                            <Text gray caption center style={{ textDecorationLine: 'underline' }}>Back to Login</Text>
                        </Text>
                    </Block>
                </Block>
            </KeyboardAvoidingView>
        )
    }
}

const styles = StyleSheet.create({
    signup: {
        flex: 1,
        justifyContent: 'center',
    },
    input: {
        borderRadius: 0,
        borderWidth: 0,
        borderBottomColor: theme.colors.gray2,
        borderBottomWidth: StyleSheet.hairlineWidth,
    },
    hasErrors: {
        borderBottomColor: theme.colors.accent,
    }

})
