import React, { Component } from 'react';
import { Animated, Dimensions, Image, Modal, FlatList, StyleSheet, ScrollView } from 'react-native';

import { Button, Block, Text, Input } from '../components';
import { theme } from '../constants';

import { Audio } from 'expo-av';

const { width, height } = Dimensions.get('window');


class Browse extends Component {

    state = {
        ingredent: '주재료 입력',
    }

    scrollX = new Animated.Value(0);

    renderIllustrations() {
        const { illustrations } = this.props;

        return (
            <FlatList
                //style={{height: height/50,}}
                horizontal
                pagingEnabled
                scrollEnabled
                showHorizontalScrollIndicator={false}
                scrollEventThrottle={100}
                snapToAlignment="center"
                data={illustrations}
                extraDate={this.state}
                keyExtractor={(item, index) => `${item.id}`}
                renderItem={({ item }) => (
                    <Image
                        source={item.source}
                        resizeMode="contain"
                        style={{ width, height: height / 4, overflow: 'visible' }}
                    />
                )}
                onScroll={Animated.event([
                    {
                        nativeEvent: { contentOffset: { x: this.scrollX } }
                    }
                ])}
            />
        )
    }

    renderSteps() {
        const { illustrations } = this.props;
        const stepPosition = Animated.divide(this.scrollX, width)
        return (
            <Block row center middle style={styles.stepsContainer}>
                {illustrations.map((item, index) => {
                    const opacity = stepPosition.interpolate({
                        inputRange: [index - 1, index, index + 1],
                        outputRange: [0.4, 1, 0.4],
                        extrapolate: 'clamp',
                    });

                    return (
                        <Block animated flex={false} key={`step-${index}`} color="gray" style={[styles.steps, { opacity }]} />
                    )
                })}
            </Block>
        )
    }




    render() {
        const { navigation } = this.props;
        return (
            <Block >
                <Block center flex={0.1}>
                    <Text>Browse</Text>
                </Block>
                <Block center top>
                    {this.renderIllustrations()}
                    {this.renderSteps()}
                </Block>
                <Block row  flex= {0.5} style={{marginTop: 20}}>
                    <Button style = {{width : width /5, marginLeft : 15}} onPress={() => navigation.navigate('Explore_korea')}>
                        <Text center> 한식 아이콘 </Text>
                    </Button>
                    <Button style={{ width: width / 5, marginLeft: 15 }} onPress={() => navigation.navigate('Explore_china')}>
                        <Text center> 중식 아이콘 </Text>
                    </Button>
                    <Button style={{ width: width / 5, marginLeft: 15 }} onPress={() => navigation.navigate('Explore_japan')}>
                        <Text center> 일식 아이콘 </Text>
                    </Button>
                    <Button style={{ width: width / 5, marginLeft: 15 }} onPress={() => navigation.navigate('Explore_europe')}>
                        <Text center> 양식 아이콘 </Text>
                    </Button>
                </Block>
                <Block row top flex={0.5}>
                    <Button style={{ width: width / 3, marginLeft: 35 }} onPress={() => navigation.navigate('Cameras')}>
                        <Text center> 카메라 아이콘 </Text>
                    </Button>
                    <Button style={{ width: width / 3, marginLeft: 60 }} onPress={() => navigation.navigate('Recoding')}>
                        <Text center> 마이크 아이콘 </Text>
                    </Button>
                </Block>
                <Block middle top margin={[0, theme.sizes.padding * 2]}>
                    <Input
                        label="주재료검색"
                        style={styles.input}
                        defaultValue={this.state.ingredent}
                        onChangeText={text => this.setState({ email: text })}
                    />
                </Block>
            </Block>
        )
    }
}

Browse.defaultProps = {
    illustrations: [
        { id: 1, source: require('../assets/images/illustration_1.png') },
        { id: 2, source: require('../assets/images/illustration_2.png') },
        { id: 3, source: require('../assets/images/illustration_3.png') },
    ],
};

export default Browse;

const styles = StyleSheet.create({
    stepsContainer: {
        position: 'absolute',
        bottom: theme.sizes.base * 2,
        right: 0,
        left: 0,
    },
    steps: {
        width: 5,
        height: 5,
        borderRadius: 5,
        marginHorizontal: 2.5,
    },

})
